import React, { Component } from "react";
import RowType from '../RowType';
import RowDetail from '../RowDetail';
import '../table.css';


class Row extends Component {
    constructor(props) {
        super(props);
        this.state = props.weekSchedule

        this.handleChange = this.handleChange.bind(this);
        this.handleArrayChange = this.handleArrayChange.bind(this);
    }

    handleChange(id, name, value) {
        // const {id, name, value} = event.target
        // this.setState({ [name]: value })
    }

    handleArrayChange(event) { //TODO add ability to add row
        const {name, value} = event.target
        const index = parseInt(name)
        const newGuides = this.state.guides.map((guide, i) => {
            return(i === index ? value : guide)
        });
        this.setState({ guides: newGuides })
    }

    render() {
        <tr className='mainRow'>
            <RowType
                time={props.row.time}
                type={props.row.type}
            />
            {props.row.days.map(day => {
                return (
                    <RowDetail
                        key = {day}
                        id = {day}
                        numGuests={day.numGuests}
                        guides={day.guides}
                    />
                )
            })}
        </tr>
    }
}

export default Row;
